custom_overrides:
@font-face {
    font-family: 'calibri';
    src: url('/files/calibri-regular.ttf') format('truetype');
}
@font-face {
  font-family: 'calibri-bold';
  src: url('/files/calibri-bold.ttf') format('truetype');
  /* Add any additional formats if needed */
}

*{
    font-family: 'calibri' !important;
}
h1,h2,h3,h4,h5,h6{
    font-family: 'calibri-bold' !important;
}
.navbar-brand img {
    max-height: 52px !important;
}
nav.navbar.navbar-light.navbar-expand-lg {
    padding: 8px 0;
}
footer.web-footer {
    background: whitesmoke;
}
.navbar-light .navbar-nav .nav-link, a.btn.btn-primary, form label {
    font-size: 1.1rem !important;
}
.carousel-caption p {
    font-size: 1.3rem;
}
.carousel-caption .item.carousel-item h4 {
    font-size: 3.2rem;
    color: white;
}
@media screen and (max-width: 768px){
nav.navbar.navbar-light.navbar-expand-lg {
    padding: 8px 11px;
}
}

